t\">\u5185\u5bb9\u6570\u91cf<\/p>\n                <\/div>\n            <\/div>\n        <\/div>\n\n    <\/div>\n<\/div>\n\n<div class=\"am-g\">\n\n\n<\/div>\n\n\n<script>\n    Do('chart', function () {\n        var webStats = <?php echo $webStats; ?>;\n        var mobileStats = <?php echo $mobileStats; ?>;\n        var apiStats = <?php echo $apiStats; ?>;\n        showData(webStats, 'web');\n        showData(mobileStats, 'mobile');\n        showData(apiStats, 'api');\n        function showData(data, type) {\n            data = getData(data);\n            new Chartist.Line('#stats-' + type, {\n                labels: data.labels,\n                series: [\n                    {\n                        data: data.nums\n                    }\n                ]\n            });\n        }\n\n        function getData(data) {\n            var labels = [];\n            var nums = [];\n            for (var key in data) {\n                labels.push(key);\n                nums.push(data[key]);\n            }\n            return {\n                'labels': labels,\n                'nums': nums\n            };\n        }\n    });\n<\/script>"}";