"><\/script>\n<script type=\"text\/javascript\" src=\"<?php echo ROOT_URL; ?>\/public\/system\/js\/lib\/do.js\" data-cfg-autoload=\"false\"><\/script>\n<script type=\"text\/javascript\" src=\"<?php echo ROOT_URL; ?>\/public\/system\/js\/lib\/init.js\" data-path=\"<?php echo ROOT_URL; ?>\/public\/system\/js\/\" data-role=\"<?php echo ROLE_NAME; ?>\" data-root=\"<?php ROOT_URL; ?>\"><\/script>\n<script>\n    $(function () {\n        Do('base', function() {\n        $('form').submit(function (e) {\n            e.preventDefault();\n            app.ajax({\n                url : '<?php echo url(); ?>',\n                type : 'post',\n                data : $('form').serialize(),\n                tip : false,\n                success : function(msg) {\n                    $('.alert').addClass('hide');\n                    window.location.href = '<?php echo url(\"system\/Index\/index\"); ?>';\n                },\n                error : function(msg) {\n                    $('.alert').removeClass('hide').text(msg);\n                }\n            });\n        });\n\n        });\n    });\n<\/script>\n<\/html>"}";